import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import org.apache.nifi.processor.io.OutputStreamCallback
import java.time.ZonedDateTime
import java.time.format.DateTimeFormatter

class GroovyOutputStreamCallback implements OutputStreamCallback {

    @Override
    void process(OutputStream outputStream) throws IOException {
        def hostname = "host"
        def domain_name = ".example.com"
        def tag = ["kernel", "python", "application"]
        def version = "1"
        def nouns = "application"
        def verbs = ["started", "stopped", "exited", "completed"]
        def adv = ["successfully", "unexpectedly", "cleanly", "gracefully"]

        (1..10).each {
            def application = "${nouns}${new Random().nextInt(10) + 1}"
            def message_id = "ID${new Random().nextInt(49) + 1}"
            def random_tag = tag[new Random().nextInt(tag.size())]
            def structured_data = "[SDID iut=\"${new Random().nextInt(9) + 1}\" eventSource=\"${random_tag}\" eventId=\"${new Random().nextInt(99) + 1}\"]"
            def time_output = ZonedDateTime.now().format(DateTimeFormatter.ISO_INSTANT)
            def random_host = new Random().nextInt(10) + 1
            def fqdn = "${hostname}${random_host}${domain_name}"
            def random_pid = new Random().nextInt(9499) + 500
            def priority = new Random().nextInt(190)
            def num = new Random().nextInt(4)
            def random_message = "${application} has ${verbs[num]} ${adv[num]}"

            def syslog_output = "<${priority}>${version} ${time_output} ${fqdn} ${application} ${random_pid} ${message_id} ${structured_data} ${random_message}\n"
            outputStream.write(syslog_output.getBytes('utf-8'))
        }
    }
}

flowFile = session.create()
if (flowFile) {
    flowFile = session.write(flowFile, new GroovyOutputStreamCallback())
    session.transfer(flowFile, REL_SUCCESS)
}

