def flowFile = session.get()
if (!flowFile) return

def url = flowFile.getAttribute('url')  // Replace 'fullURL' with the name of your URL attribute

// Remove the 'http://' or 'https://'
url = url.replaceAll("http://", "").replaceAll("https://", "")

def segments = url.split('/')

def hdfsPath = '/tmp'  // Replace this with your HDFS root directory
def fileName = ''

segments.eachWithIndex { segment, index ->
    flowFile = session.putAttribute(flowFile, "segment${index}", segment)
    if (index < segments.size() - 1) {
        hdfsPath = "${hdfsPath}/${segment}"
    } else {
        fileName = segment
    }
}

// Remove '.html' from the end of fileName if it exists
if (fileName.endsWith('.html')) {
    fileName = fileName.substring(0, fileName.length() - 5)
}

// Add '.txt' to the end of fileName
fileName = "${fileName}.txt"

flowFile = session.putAttribute(flowFile, 'HDFSPath', hdfsPath)
flowFile = session.putAttribute(flowFile, 'HDFSFileName', fileName)

session.transfer(flowFile, REL_SUCCESS)
